interface SinCosTable {
  /**
   * The sine value.
   */
  sin: number;
  /**
   * The cosine value.
   */
  cos: number;
  /**
   * The length.
   */
  length: number;
}

interface Vector2Like {
  /**
   * The x component.
   */
  x?: number;
  /**
   * The y component.
   */
  y?: number;
}

interface Vector3Like {
  /**
   * The x component.
   */
  x?: number;
  /**
   * The y component.
   */
  y?: number;
  /**
   * The z component.
   */
  z?: number;
}

interface Vector4Like {
  /**
   * The x component.
   */
  x?: number;
  /**
   * The y component.
   */
  y?: number;
  /**
   * The z component.
   */
  z?: number;
  /**
   * The w component.
   */
  w?: number;
}